class Solution {
public:
    int maxCount(vector<int>& banned, int n, int maxSum) {
        unordered_map<int,int>mp;
        //storing all the banned integers in an unordered_map
        for(auto it:banned){
            mp[it]++;
        }
        vector<int>ans;
        //inserting all the possible integers in our vector named ans
        for(int i=1;i<=n;i++){
            if(mp.find(i)==mp.end())ans.push_back(i);
        }
        int cnt=0;
        int sum=0;
        //checking if the running sum exceeds the maxSum & finding the feasible solutions 
        for(int i=0;i<ans.size();i++){
            sum+=ans[i];
            if(sum<=maxSum)cnt++;
        }
        return cnt;
    }
};

```

