    int fun3(vector<int>&banned,int &n,int &maxsum)
    {
        sort(banned.begin(),banned.end());
        int sum = 0;
        int count = 0;
        int num = 1;
        while(num <= n)
        {
            bool found = binary_search(banned.begin(),banned.end(),num);
            if(found == true)
            {
                num++;
            }
            else if(found == false)
            {
                if((sum + num) > maxsum)
                return count;

                sum = sum + num;
                count++;
                num++;
            }
        }
        return count;
    }
    int fun2(vector<int>&banned,int &n,int &maxsum)
    {
        sort(banned.begin(),banned.end());
        int sum = 0;
        int count = 0;
        int num = 1;
        while(num <= n)
        {
            bool found = getpos(num,banned);
            if(found == true)
            {
                num++;
            }
            else if(found == false)
            {
                if((sum + num) > maxsum)
                return count;

                sum = sum + num;
                count++;
                num++;
            }
        }
        return count;
    }
    bool getpos(int num,vector<int>&banned)
    {
         int start = 0;
         int end = banned.size()-1;

         while(start <= end)
         {
             int mid = (start + end)/2;

             if(banned[mid] == num)
             {
                 return true;
             }
             else if(num > banned[mid])
             {
                 start = mid+1;
             }
             else
             {
                 end = mid-1;
             }
