class Solution {
public:
    int maxCount(vector<int>& banned, int n, int maxSum) {
        vector<int> v(n);
        for(int i=0; i<n; i++){
            v[i] = i+1;
        }
        for(int i=0; i<banned.size(); i++){
            if (banned[i] <= n){
                v[banned[i]-1] = 0;
            }
        }
        v.erase(remove(v.begin(), v.end(), 0), v.end());
        int temp = 0;
        int i =0;
        for(i; i<v.size(); i++){
            if(temp + v[i] <= maxSum){
                temp += v[i];
            }else{
                break;
            }
        }
        return i;
    }
};
```

