class Solution {
    public int maxCount(int[] banned, int n, int maxSum) {
        Set<Integer>checkNum = new HashSet<>();
        for(int x:banned) checkNum.add(x);
        int cnt=0,sum=0;
        for(int i=1;i<=n;i++){
            if(checkNum.contains(i)) continue;
            if((sum+i)>maxSum) return cnt;
            cnt++;
            sum+=i;
        }
        return cnt;
    }
}
