    public static boolean bs(int ar[],int lo,int hi,int target){
        while(hi>=lo){
            int mid = lo+(hi-lo)/2;
            if(ar[mid]>target){
                hi=mid-1;
            }
            else if (ar[mid]<target){
                lo=mid+1;
            }
            else{
                return true;
            }

        }
        return false;
