        int ct=0;
        for(int i =0;i<n;i++){
            if( arr[i] <= maxSum){
                if (bs(banned,0,banned.length-1,arr[i])==false){
                    ct++;
                    maxSum-=arr[i];
                }

            }
        }
        return ct;
    }
}
