        unordered_map<int,int>mp;
        for(int i=0;i<v.size();i++)mp[v[i]]++;
        for(int i=0;i<p.size();i++)if(mp.find(p[i])==mp.end())q.push_back(p[i]);
        int k=accumulate(q.begin(),q.end(),0);
        if(k<=sum)return q.size();
        for(int i=0;i<q.size();i++){
            if(x>=sum)break;
            x+=q[i];
            z++;
        }
        return z-1;
    }
};
