class Solution {
public:
    bool search(vector<int> & banned,int x){
        int i=0;
        int j=banned.size()-1;
        while(i<=j){
            int mid=(i+j)/2;
            if(banned[mid]==x){
                return true;
            }
            else if(banned[mid] < x){
                i=mid+1;
            }
            else{
                j=mid-1;
            }
        }
        return false;
    }
    int maxCount(vector<int>& banned, int n, int maxSum) {
        sort(banned.begin(),banned.end());
        int sum=0;
        int count=0;
        for(int i=1;i<=n;i++){
            if(search(banned,i)==false && sum +i <=maxSum){
                sum+=i;
                count++;
            }
        }
        return count;
    }
};
