use std::collections::*;

impl Solution {
  pub fn max_count(banned: Vec<i32>, n: i32, mut max_sum: i32) -> i32 {
    let banned = banned.into_iter().collect::<HashSet<i32>>();
    let mut result = 0;
    for v in 1..=n {
      if !banned.contains(&v) {
        if v <= max_sum {
          result += 1;
          max_sum -= v;
        } else {
          break
        }        
      }
    }

    result
  }
}
```

