/**
 * @param {number[]} banned
 * @param {number} n
 * @param {number} maxSum
 * @return {number}
 */
var maxCount = function(banned, n, maxSum) {
    let set = new Set(new Array(n).fill().map((_, i) => i + 1));
    let sum = 0;
    let count = 0;

    for(let ban of banned)
        set.delete(ban);

    //  convert Set to Array
    let arr = [...set];
    let idx = 0;
    while(sum < maxSum){
        if((sum + arr[idx]) <= maxSum)
            count++;

        sum += arr[idx];
        idx++;
    }

    return count;
};
