class Solution {
public:
    int binarySearch(vector<int>& vec, int l, int r, int val) {
        while (l <= r) {
            int mid = (l+r)/2;
            if (vec[mid] == val) return mid;
            else if (vec[mid] > val) {
                r = mid-1;
            } else if (vec[mid] < val) {
                l = mid+1;
            }
        }
        return -1;
    }

    int maxCount(vector<int>& banned, int n, int maxSum) {
        int ans = 0, r=0;
        vector<int> vec;

        for (int i=0; i<banned.size(); i++) {
            if (binarySearch(vec, 0, vec.size()-1, banned[i]) == -1) vec.push_back(banned[i]);
        }

        sort(vec.begin(), vec.end());
        int sz = vec.size();

        for (int i=1; i<=n; i++) {
            if (binarySearch(vec, 0, sz-1, i) == -1) {
                ans += i;
                if (ans <= maxSum) r++;
            }
        }

        return r;
    }
};
```

