        int sum = 0;
        int count = 0;
        for(int i=1; i<=n; i++){
            if(!uset.count(i) && sum < maxSum){
                count++;
                sum += i;
                if(sum > maxSum) return count-1;
            }
        }
        return count;

    }
};
