class Solution {
public:
  bool inarr(int i,vector<int>& arr){
        int low = 0;
        int high = arr.size()-1;
        while(low<=high){
            int mid = (high - low)/2 + low;
            if(arr[mid]==i){
                return true;
            }
            else if(arr[mid]<i){
                low=mid+1;
            }
            else{
                high = mid-1;
            }
        }
        return false;
    }
    int maxCount(vector<int>& banned, int n, int maxSum) {
        sort(banned.begin(),banned.end());
        int count=0;
        int i=1;
        int sum =0;
        while(i<=n){
            if(inarr(i,banned)){
                i++;
            }
            else{
                if(sum+i<=maxSum){
                    count++;
                    sum = sum+i;
                }
                i++;
            }
        }
        return count;
    }
};



```

