long long countFairPairs(vector<int>& v, int lower, int upper) {
        sort(v.begin(),v.end());
        int n=v.size();
        long long ans=0;
        for(int i=0;i<n;i++){
            int a=lower_bound(v.begin()+i+1,v.end(),lower-v[i])-v.begin();
            int b=upper_bound(v.begin()+i+1,v.end(),upper-v[i])-v.begin();
            if(a==n||v[i]+v[a]>upper) continue;
            if(a==b&&a!=i) {
                ans++;
                continue;
            }
            ans+=(b-a);
        }
        return ans;
    }
