I prefer mine one.**

```
class Solution
{
    public:
        int bsh(vector<int> &nums, int x, int num, int l, int h)//binary search for finding higher bound
        {
            int best = nums.size();
            while (l <= h)
            {
                int mid = l + (h - l) / 2;
                if (nums[mid] + num <= x)
                {
                    best = mid;
                    l = mid + 1;
                }
                else
                    h = mid - 1;
            }
            return best;
        }
    int bsl(vector<int> &nums, int x, int num, int l, int h)//binary search for finding lower bound
    {
        int best = nums.size();
        while (l <= h)
        {
            int mid = l + (h - l) / 2;
            if (nums[mid] + num >= x)
            {
                best = mid;
                h = mid - 1;
            }
            else
                l = mid + 1;
        }
        return best;
    }
    long long countFairPairs(vector<int> &nums, int lower, int upper)
    {
        sort(nums.begin(), nums.end());
        long long ans = 0;
        for (int i = 0; i < nums.size(); i++)
        {
            int l = bsl(nums, lower, nums[i], i + 1, nums.size() - 1);
            int h = bsh(nums, upper, nums[i], i + 1, nums.size() - 1);
            if (l != nums.size() and h != nums.size())//if found valid index then calculate their intermediate distance and add to answer.
                ans += (h - l + 1);
        }
        return ans;
    }
};
```

