class Solution {
    func countFairPairs(_ nums: [Int], _ lower: Int, _ upper: Int) -> Int {

        var res = 0
        let nums = nums.sorted()

        var l = 0
        var r = nums.count - 1

        while l < r {
            if nums[l] + nums[r] <= upper {
                res += r - l
                l += 1
            } else {
                r -= 1
            }
        }

        l = 0
        r = nums.count - 1

        while l < r {
            if nums[l] + nums[r] <= lower - 1 {
                res -= r - l
                l += 1
            } else {
                r -= 1
            }
        }

        return res
    }
}
