class Solution {
    func countFairPairs(_ nums: [Int], _ lower: Int, _ upper: Int) -> Int {
        let nums = nums.sorted()
        var result = 0, left = 0, rightL = nums.count-1, rightU = rightL
        while left < max(rightL, rightU) {
            while left < rightU && upper < nums[left] + nums[rightU] { rightU -= 1 }
            while left < rightL && lower <= nums[left] + nums[rightL] { rightL -= 1 }
            result += max(rightU-left, 0) - max(rightL-left, 0)
            left += 1
        }
        return result
    }
}
