class Solution {
public:
    long long countFairPairs(vector<int>& nums, int lower, int upper) {
        // lower - nums[i] <= nums[j] <= upper - nums[i]
        //To find the upper and lowe bound we should sort the array
        //As of the condition i < j, the lower and upper bound should not point to the previous elements of the current element 

        long long ans = 0;
        sort(nums.begin(), nums.end());
        deque<long long> dq;
        for(auto i:nums) dq.push_back(i);

        for(int i=0;i<nums.size();i++) {
            dq.pop_front(); // To satisfy i < j condition as i said earlier

            auto low = lower_bound(dq.begin(), dq.end(), lower - nums[i]);  // lower - nums[i] <= nums[j] <= upper - nums[i]
            auto high =  upper_bound(dq.begin(), dq.end(), upper - nums[i]);

            ans += (high - low); //Difference between high to low gives us the numbers in range

        }
        return ans;
    }
};
