```go
func countFairPairs(nums []int, lower int, upper int) int64 {
	sorted := make([]int, 0, 16)
	count := 0
	for _, v := range nums {
		lBound := findFirstGreaterOrEqual(sorted, lower-v)
		rBound := findFirstGreaterOrEqual(sorted, upper-v+1)
		count += rBound - lBound
		insertIndex := findFirstGreaterOrEqual(sorted, v)
		sorted = append(sorted, 0)
		copy(sorted[insertIndex+1:], sorted[insertIndex:])
		sorted[insertIndex] = v
	}
	return int64(count)
}

func findFirstGreaterOrEqual(nums []int, val int) int {
	l, r := 0, len(nums)-1
	for l <= r {
		m := (l+r)>>1
		if nums[m] < val {
			l = m+1
		} else {
			r = m-1
		}
	}
	return r+1
}
