class Solution {
public:
    long long countFairPairs(vector<int>& nums, int lower, int upper)
    {
        sort(nums.begin(), nums.end());
        long long ans = 0;
        int n = nums.size();
        for(int i=0;i<n-1;i++)
        {
            if(2*nums[i]<=upper && nums[i]+nums[n-1]>=lower)
            {
                int l1 = i+1;
                int r1 = n-1;
                while(l1<r1)
                {
                    int m1 = l1+(r1-l1)/2;
                    if(nums[i]+nums[m1]>=lower){r1=m1;}
                    else{l1=m1+1;}
                }
                int l2 = l1;
                int r2 = n;
                while(l2<r2)
