                    int m2 = l2+(r2-l2)/2;
                    if(nums[i]+nums[m2]>upper){r2=m2;}
                    else{l2=m2+1;}
                }
                l2--;
                ans=ans+l2-l1+1;
            }
        }
        return ans;
    }
};
