var countFairPairs = function(nums, lower, upper) {
    nums.sort((b, a) => b - a);

    let fairPairs = 0, n = nums.length;

    for (let i = 0, l = n, u = n - 1; i < u; l = Math.max(l, ++i + 1)) {
        while (i < l - 1 && nums[i] + nums[l - 1] >= lower) l--;
        while (i < u && nums[i] + nums[u] > upper) u--;
        fairPairs += u - l + 1;
    }

    return fairPairs;
};
```

