class Solution {
public:
    long long countFairPairs(vector<int>& v, int lower, int upper) {
       int n = v.size();

        sort(v.begin(),v.end());

        long long  i = 0 , j = n-1,cnt = 0;

        while(i<j)
        {
            if(v[i]+v[j] > upper)
            {
                j--;
            }
            else if(v[i]+v[j] < lower)
            {
                i++;
            }

            else if(v[i]+v[j] >= lower and v[i]+v[j] <= upper)
            {
                // now finding the min val that is also satisfy the given condn
                int k = i+1;
                while(k<=j)
                {
                    if(v[k]+v[i] >= lower)
                    {
                        break;
                    }
                    else 
                    {
                        k++;
                    }
                }

                cnt += (j-k+1); // including val pair with i that satisfy this condn
                i++;
            }

        }


        return cnt;
    }
};




