class Solution {
public:
    long long toN(vector<int>& v, int limit){
        long long cnt = 0;
        for(int i = 0, j = v.size() - 1 ; i < j; i++){
            while(i < j && v[i] + v[j] > limit)
                --j;
            cnt += j - i;
        }
        return cnt;
    }
    long long countFairPairs(vector<int>& nums, int lower, int upper) {
        sort(nums.begin(), nums.end());
        return toN(nums, upper) - toN(nums, lower  - 1);
    }
};
```

