class Solution {
private:
    int searchUpper(int x, int start, int end, int upper, vector<int>& arr){
        int ans = -1;
        while(start<=end){
            int mid = (start+end)/2;
            if(x + arr[mid] <= upper){
                start = mid+1;
                ans = mid;
            }
            else{
                end = mid-1;
            }
        }
        return ans;
    }
    int searchLower(int x, int start, int end, int lower, vector<int>& arr){
        int ans = -1;
        while(start<=end){
            int mid = (start+end)/2;
            if(x + arr[mid] >= lower){
                end = mid-1;
                ans = mid;
            }
            else{
                start = mid+1;
            }
        }
        return ans;
    }

public:
    long long countFairPairs(vector<int>& arr, int lower, int upper) {
        int n = arr.size();
        long long ans=0;
        sort(arr.begin(), arr.end());
        for(int i=0; i<n; i++){
            int l = searchLower(arr[i], i+1, n-1, lower, arr);
            int u = searchUpper(arr[i], i+1, n-1, upper, arr);
            cout<<l<<" "<<u<<endl;
            if(l!=-1 && u!=-1){
                ans += (u-l+1);
            }
        }
        return ans;

    }
};
