        for(int i = 0; i < n; i++) {
            int d1 = s.order_of_key(upper - nums[i] + 1);
            int d2 = s.order_of_key(lower - nums[i]);
            ans += (d1 - d2);
            s.insert(nums[i]);
        }
        return ans;
    }
};
