class Solution {
    int findupperindex(vector<int>& arr,int target){
        int s=0,e=arr.size()-1;
        int mid;
         while(s<=e){
            // cout<<s<<" "<<e<<endl;
            mid=s+(e-s)/2;

          if(target>=arr[mid]){
                s=mid+1;
            }
            else {
                e=mid-1;
                }            
        } 

        return s; 
    }

    int findlowerindex(vector<int> &arr,int target){

        int s=0,e=arr.size()-1;
        int mid;
        while(s<=e){
            // cout<<s<<" "<<e<<endl;
            mid=s+(e-s)/2;

          if(target>arr[mid]){
                s=mid+1;
            }
            else {
                e=mid-1;
                }            
        }
return e;
    }
public:
    long long countFairPairs(vector<int>& nums, int lower, int upper) {

        sort(nums.begin(),nums.end());
       long long ans=0;
        for(int i=0;i<nums.size();i++){
            int lowerval=lower-nums[i];
            int upperval=upper-nums[i];
            int lowerindex=findlowerindex(nums,lowerval);
            int upperindex=findupperindex(nums,upperval);
            ans+=upperindex-lowerindex-1;
            if(i>lowerindex&& i<upperindex)
            ans-=1;
            // cout<<upperindex<<" "<<lowerindex<<endl; 
        }

return ans/2;

    }
};


