class Solution {
    private int getMax(int[] nums, int target, int start) {
        int low = start, high = nums.length - 1;
        while(low <= high) {
            int mid = (low + high) / 2;

            if(target < nums[mid]) {
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return high;
    }
    private int getMin(int[] nums, int target, int start) {
        int low = start, high = nums.length - 1;
        while(low <= high) {
            int mid = (low + high) / 2;

            if(target <= nums[mid]) {
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return low;
    }
    public long countFairPairs(int[] nums, int lower, int upper) {
        Arrays.sort(nums);
        long ans = 0;
        for(int i = 0; i < nums.length; i++) {
            int minIdx = getMin(nums, lower - nums[i], i+1);
            int maxIdx = getMax(nums, upper - nums[i], i+1);

            //Finding a valid pairs
            if(minIdx > i && maxIdx > i) {
                if(minIdx <= maxIdx) {
                    ans += (long)((maxIdx - minIdx) + 1);
                }
            }
        }
        return ans;
    }
}
