class Solution {
    public long countFairPairs(int[] nums, int lower, int upper) {
       // for every element we have to find the elements which is >= lower and <= upper
       // we will sort the array,once sorted we can see for eche element which is the starting
       // and ending points
        if(nums.length==1) return 0;
        Arrays.sort(nums);

        Map<Integer,Integer> startIndexMap = new HashMap<>();
        Map<Integer,Integer> endIndexMap = new HashMap<>();
        TreeMap<Integer,Integer> orderedMap = new TreeMap<>();

        long ans = 0;

        int startIndex=0,endIndex = 0;
        int ind = 0;
        for(int i : nums){
            //let's create the range if we thake i then we have find value(j) which is >= lower - i and <= upper - i
            //how?..think if we have to make one pair(i,j) ---> where lower >= i+j <= upper
            //so if we think every i creates pair then lower - i >= j <= upper - i
            //once the map prepared we just have to find the boundary indexes
            if(!startIndexMap.containsKey(i)){

                startIndexMap.put(i,ind);

            }

            endIndexMap.put(i,ind);

            //get the boudaries as the number previously

            Integer lowerBoundary = orderedMap.ceilingKey(lower - i);//start
            Integer upperBoundary = orderedMap.floorKey( upper - i);//end

            if(lowerBoundary != null && upperBoundary != null ){
                //get the indexes from index maps for getting how many mubers are there inside that boundary
                ans += endIndexMap.get(upperBoundary) - startIndexMap.get(lowerBoundary) ;
                if(upperBoundary != i){
                    ans+=1;
                }
            }
            orderedMap.put(i,0);

            ind++;


        }


        return ans;
    }
}
