class Solution {
    public long countFairPairs(int[] nums, int lower, int upper) {
        long count=0;
        if(nums.length==1) return count;
        Arrays.sort(nums);
        TreeMap<Integer,Integer> map1 = new TreeMap<>();
        TreeMap<Integer,Integer> map2 = new TreeMap<>();
        for(int i=0;i<nums.length;i++){
            if(!map1.containsKey(nums[i])) map1.put(nums[i],i);
            map2.put(nums[i],i);
            Integer l = map2.ceilingKey(lower-nums[i]);
            Integer r = map2.floorKey(upper-nums[i]);
            if(l==null || r==null || l>r) continue;
            count+=map2.get(r)-map1.get(l);
            if(map2.get(r)!=i) count++;
        }
        return count;
    }
}
