class Solution {
public:
    long long merge(int lo, int mid, int hi, vector<int>& nums, int lower, int upper) {
        long long ans = 0;

        // Geeting the start Index and the end Index of the elements which satify the given conditions.
        for(int i = lo; i <= mid; i++) {

            // Using Binary Search to get the start and end Index.
            int startIdx = lower_bound(nums.begin() + mid + 1, nums.begin() + hi + 1, lower - nums[i]) - nums.begin(); 
            int endIdx = upper_bound(nums.begin() + mid + 1, nums.begin() + hi + 1, upper - nums[i]) - nums.begin();
            ans += 1LL * (endIdx - startIdx);
        }

        // Merging two sorted array.
        vector<int>curr;
        int s = lo, e = mid + 1;
        while(s <= mid and e <= hi) {
            if(nums[s] < nums[e]) {
                curr.push_back(nums[s]);
                s++;
            }
            else {
                curr.push_back(nums[e]);
                e++;
            }
        }
        while(s <= mid) {
            curr.push_back(nums[s]);
            s++;
        }
        while(e <= hi) {
            curr.push_back(nums[e]);
            e++;
        }
        int idx = 0;
        for (int i = lo; i <= hi; i++) {
            nums[i] = curr[idx];
            idx++;
        }
        return ans;
    }
    long long mergeSort(int lo, int hi, vector<int>& nums, int lower, int upper) {
        if(lo >= hi) {
            return 0;
        }
        int mid = (hi + lo)/2;

        // Calling MergeSort from lo to mid
        long long ans = mergeSort(lo, mid, nums, lower, upper);

        // Calling MergeSort from mid + 1 to hi
        ans += mergeSort(mid + 1, hi, nums, lower, upper);

        // Merging the two sorted array.
        ans += merge(lo, mid, hi, nums, lower, upper);
        return ans;
    }
    long long countFairPairs(vector<int>& nums, int lower, int upper) {
        return mergeSort(0, nums.size() - 1, nums, lower, upper);
    }
};
