class Solution {
    func countFairPairs(_ nums: [Int], _ lower: Int, _ upper: Int) -> Int {
        let n = nums.count
        guard n > 1 else { return 0 }
        let sorted = nums.sorted(by: <)
        var ans = 0

        func countPairSumLess(_ target: Int) -> Int {
            var count = 0
            for i in 0..<n-1 {
                if sorted[i] + sorted[i+1] > target { continue }
                var left = i+1
                var right = n-1
                var j = left
                while left <= right {
                    let mid = left + (right - left) / 2 
                    if sorted[i] + sorted[mid] > target {
                        right = mid-1
                    } else {
                        j = mid
                        left = mid+1
                    }
                }
                count += j-i
            }

            return count
        }

        return countPairSumLess(upper) - countPairSumLess(lower-1)
    }
}
