class Solution {
public:
    long long countFairPairs(vector<int>& nums, int lower, int upper) {
        long long ans = 0;
        int n = (int)nums.size();

        sort(nums.begin(), nums.end());

        for (int i = 0; i < n - 1; ++i) {
            int newLower = lower - nums[i];
            int newUpper = upper - nums[i];
            int lb = lower_bound(nums.begin() + i + 1, nums.end(), newLower) - nums.begin();
            int ub = upper_bound(nums.begin() + i + 1, nums.end(), newUpper) - nums.begin();
            ans += ub - lb;
        }

        return ans;
    }
};
```

