class Solution {
    public long countFairPairs(int[] nums, int lower, int upper) {
        Arrays.sort(nums);
        // 0 1 4 4 5 7
        long ans = 0;
        for(int i = 0;i<nums.length;i++){
            int index = binarySearchUpper(i,nums,upper);
            // System.out.println("index: "+ index);
            int index2 = binarySearchLower(i,nums,lower);
            index2++;
            int pairs = index - index2 + 1;
            // System.out.println("nums[i] " + nums[i] + " pairs: " + pairs);
            if(i>=index2 && i<=index)pairs--;
            ans+=pairs;
            // System.out.println("index2: "+ index2);
        }
        return ans/2;
    }

    public int binarySearchUpper(int i,int [] nums,int upper){
        int toFind = upper - nums[i];
        // System.out.println("To find:" + toFind);
        int k = 0,j = nums.length - 1;
        int ans = -1;
        while(k<=j){
            int mid = (k+j)/2;
            if(nums[mid]<=toFind){
                ans = mid;
                k = mid + 1;
            }else j = mid - 1;
        }
        return ans;
    }

    public int binarySearchLower(int i,int [] nums,int lower){
        int toFind = lower - nums[i];
        // System.out.println("To find:" + toFind);
        int k = 0,j = nums.length - 1;
        int ans = -1;
        while(k<=j){
            int mid = (k+j)/2;
            // System.out.println("mid " + mid + " k: " + k + " j: " + j);
            if(nums[mid]<toFind){
                // System.out.println("lower:" + mid);
                ans = mid;
                k = mid + 1;
            }else j = mid - 1;
        }
        return ans;
    }
}
