        {
            for(int i=0;i<len;i++){
                if(str[index] == str[i]){
                    bi[i]='9';
                }
            }
        }

        //swapping all occurrences of 0th index to '0'
        //to get smaller number
        for(int i=0;i<len;i++){
            if(str[0] == sm[i]){
                sm[i]='0';
            }
        }
        int diff = stoi(bi)-stoi(sm);
        return diff;
    }
};
