class Solution {
public:
    int minMaxDifference(int num) {
        string s = to_string(num);
        string x=s;
        int i=0;
        while(i<x.size() && x[i]=='9'){
                i++;
        }
        char a= i<x.size()?s[i]:'.';
        if(a=='.') return stoi(x);
        for(;i<x.size();i++){
            if(x[i]==a) x[i] = '9';
        }
        string xx=s;
        int j=0;
        while(j<xx.size() && xx[j]=='0'){
            j++;
        }
        char b = j<xx.size()?xx[j]:'.';
        if(b=='.'){
            string ans="";
            for(int m=0;m<s.size();m++) ans+='9';
            return stoi(ans);
        }
        for(;j<xx.size();j++){
            if(xx[j]==b){
                xx[j] = '0';
            }
        }
        return stoi(x)-stoi(xx);
    }
};
