class Solution {
    public int minMaxDifference(int num) {
        char c[] = String.valueOf(num).toCharArray();
        int idx = -1;
        for(int i = 0;i < c.length;i++){
            if(c[i] != '9'){
                idx = i;
                break;
            }
        }
        if(idx == -1) return num;
        int max = Integer.parseInt(String.valueOf(num).replaceAll("" + c[idx],"9"));
        int min = Integer.parseInt(String.valueOf(num).replaceAll("" + c[0],"0"));
        return max - min;
    }
}
