class Solution:
    def minMaxDifference(self, num: int) -> int:

        def get_maxmin_value(pos: int, value: int):
            num_copy = str(num)
            return int( num_copy.replace( num_copy[pos], str(value) ) )

        min_value, max_value = 0, 0
        max_pos, min_pos = 0, 0

        # Find the first non-nine digit to get the max_pos
        # Remaping the first digit with zero will end up to the min value, So min_pos always will be 0.
        for max_pos, i in enumerate(str(num)):
            if i != "9":
               break

        # Get the Max Value
        max_value = get_maxmin_value(max_pos, 9)

        # Get the Min Value
        min_value = get_maxmin_value(min_pos, 0)

        return max_value - min_value
