var minMaxDifference = function (num) {
  const numStr = num.toString();
  let maximum = num;
  let minimum = num;

  for (let i = 0; i <= 9; i++) {
    const replaced = numStr.replace(new RegExp(i, 'g'), '9');
    maximum = Math.max(maximum, parseInt(replaced));

    if (i !== 0) {
      const replacedWithZero = numStr.replace(new RegExp(i, 'g'), '0');
      minimum = Math.min(minimum, parseInt(replacedWithZero));
    }
  }

  return maximum - minimum;
}
