class Solution {
public:
    int minMaxDifference(int num) {
        int maxNum = getMaxMinNum(num, '9');
        int minNum = getMaxMinNum(num, '0');
        return maxNum - minNum;
    }

    int getMaxMinNum(int num, char search) {
        string strNum = to_string(num);
        int i = 0;
        while (i < strNum.size() && strNum[i] == search) {
            i++;
        }
        if (i >= strNum.size()) {
            return num;
        }
        char digit = strNum[i];
        for (int j = 0; j < strNum.size(); j++) {
            if (strNum[j] == digit) {
                strNum[j] = search;
            }
        }
        return stoi(strNum);
    }
};

