    const to0 = digits[0]
    const max = digits.map(n => n === to9 ? 9 : n)
    const min = digits.map(n => n === to0 ? 0 : n)
    return Number(max.join('')) - Number(min.join(''))
};

function toDigits(n: number): number[] {
    const result: number[] = []
    while (n > 0) {
        result.push(n % 10)
        n = Math.floor(n / 10)
    }
    return result.reverse()
}
// 
