function minMaxDifference(num: number): number {
    const digits = [];
    let maxDigit = 9;
    let minDigit = 0;
    let maxNum = 0;
    let minNum = 0;

    // convert 12345 into [1, 2, 3, 4, 5]
    while (num > 0) {
        // "unshift" is fine here since max digits is 8
        digits.unshift(num % 10);
        num = Math.floor(num / 10);
    }

    for (let i = 0; i < digits.length; i++) {
        const digit = digits[i];
        const parsedNum = digit % 10;

        // find digit which should be replaced with 9 (max) and 0 (min)
        if (parsedNum !== 9 && maxDigit === 9) {
            maxDigit = parsedNum;
        }

        if (minDigit === 0) {
            minDigit = parsedNum;
        }

        maxNum *= 10;
        minNum *= 10;

        if (digit === maxDigit) {
            maxNum += 9;
        } else {
            maxNum += digit;
        }

        if (digit === minDigit) {
            minNum += 0;
        } else {
            minNum += digit;
        }
    }

    return maxNum - minNum;
};
```

