class Solution {
    private int length(int num) {
        return (int)Math.floor(Math.log10(num) + 1);
    }
    private int nth(int num, int pos) {
        return num / (int)Math.pow(10, pos) % 10;
    }
    private int remapDigit(int num, int from, int to) {
        int res = 0, base = 1;
        while(num > 0) {
            int d = num % 10;
            res += base * (d == from ? to: d);
            num /= 10;
            base *= 10;
        }
        return res;
    }

    public int minMaxDifference(int num) {
        int len = length(num);
        int first = nth(num, --len);
        int min = remapDigit(num, first, 0);
        while(first == 9 && len > 0) {
            first = nth(num, --len);
        }
        return remapDigit(num, first, 9) - min;
    }
}
