class Solution {
    public int minMaxDifference(int num) {
        char[] numArr = String.valueOf(num).toCharArray();
        return getMax(num, numArr) - getMin(num, numArr);
    }

    private int getMin(int num, char[] numArr) {
        int min = Integer.MAX_VALUE;
        int numChosen = 0;

        for (char ch: numArr) {
            int chNum = Character.getNumericValue(ch);
            int remappedNumber = remapNumber(num, chNum, 
            numChosen, numArr);
            min = Math.min(min, remappedNumber);
        }

        return min;
    }

    private int getMax(int num, char[] numArr) {
        int max = Integer.MIN_VALUE;
        int numChosen = 9;

        for (char ch: numArr) {
            int chNum = Character.getNumericValue(ch);
            int remappedNumber = remapNumber(num, chNum, 
            numChosen, numArr);
            max = Math.max(max, remappedNumber);
        }

        return max;
    }

    private int remapNumber(int num, 
    int numToRemap, int numChosen, char[] numArr) {
        StringBuilder build = new StringBuilder();

        for (char ch: numArr) {
            int chNum = Character.getNumericValue(ch);

            if (chNum == numToRemap)
                build.append(numChosen);
            else
                build.append(ch);
        }

        int toNum = Integer.valueOf(build.toString());
        return removeLeadingZeros(toNum);
    }

    private int removeLeadingZeros(int num) {
        int counter = 0;
        String numStr = String.valueOf(num);
        int n = numStr.length();

        while (n > 1 && numStr.charAt(0) == '0')
            counter++;

        return Integer.valueOf(numStr.substring(counter, n));
    }
}
