function minMaxDifference (num) {
    const digits = num.toString().split('');
    const maxNumber = join(replaceWith(digits, '9'));
    const minNumber = join(replaceWith(digits, '0'));
    return maxNumber - minNumber;
};

function replaceWith(originalDigits, replaceWith) {
    let digitToReplace = null;
    const digits = [...originalDigits];

    for (let i = 0; i < digits.length; i++) {
        const thisDigit = digits[i];

        // E.g. if the number starts with 9 like 99199, do not replace 9 with 9
        if (digitToReplace === null && thisDigit === replaceWith) {
            continue;
        }

        // Find the first digit that is not "replaceWith"
        if (digitToReplace === null) {
            digitToReplace = thisDigit;
        }

        if (thisDigit === digitToReplace) {
            digits[i] = replaceWith;
        }
    }

    return digits;
}

function join(digits) {
    return Number(digits.join(''));
}

```

