class Solution {
public:
    int minMaxDifference(int num) {
        int ref1=-1, ref2=-1, max=0, min=0;
        vector<int> arrmax;
        vector<int> arrmin;
        while(num){
            arrmax.push_back(num%10);
            if(num%10!=9) ref1 = num%10;
            arrmin.push_back(num%10);
            if(num%10!=0) ref2 = num%10;
            num/=10;
        }
        for(int i=0; i < arrmax.size(); i++){
            if(ref1!=-1){
                if(arrmax[i]==ref1) arrmax[i] = 9;
            }
            if(ref2!=-1){
                if(arrmin[i]==ref2) arrmin[i] = 0;
            }
        }
        for(int i=arrmax.size()-1, j=0; i > -1; i--, j++){
            max = max*10 + arrmax[i];
            min = min*10 + arrmin[i];
        }
        return max-min;
    }
};
```

