```
int minMaxDifference(int num){
	assert(num >= 0);

	int8_t digits[10];
	int digitsLen = 0;
	while (num > 0){
		digits[digitsLen] = num % 10;
		digitsLen += 1;

		num /= 10;
	}

	for (int i = digitsLen - 1; i >= 0; i -= 1){
		if (digits[i] < 9){
			const int oldDigit = digits[i];
			for (int j = 0, weight = 1; j <= i; j += 1, weight *= 10){
				if (oldDigit == digits[j]){
					num += weight * (9 - oldDigit);
				}
			}
			break;
		}
	}

	if (digitsLen - 1 >= 0){
		const int oldDigit = digits[digitsLen - 1];
		for (int i = 0, weight = 1; i < digitsLen; i += 1, weight *= 10){
			if (oldDigit == digits[i]){
				num += weight * oldDigit;
			}
		}
	}

	return num;
}
```

