# Intution and Approach
<!-- Describe your approach to solving the problem. -->
Build a lazy segment tree to handle type1 queries and in both type2 and type3 queries we dont need any updates to be actually done on nums2 instead just maintaining overall sum is enough.So finally for every type2 query getting the no of 1s in the overall nums1 array is enough.
# Complexity
- Time complexity:
<!-- Add your time complexity here, e.g. $$O(n)$$ -->
O(NLOGN)
- Space complexity:
<!-- Add your space complexity here, e.g. $$O(n)$$ -->
O(NLOGN)

# Code
```
#include <bits/stdc++.h>
using namespace std;

struct segmentTreeLazy
{
    int n;
    vector<long long> st, lazy;

    void build(int start, int ending, int node, vector<int> &v)
    {
        if (start == ending)
        {
            st[node] = v[start];
            return;
        }

        int mid = (start+ending) / 2;
        // left subtree is (start, mid)

        build(start, mid, 2 * node + 1, v);

        // right subtree is (mid+1, ending)

        build(mid + 1, ending, 2 * node + 2, v);

        st[node] = st[node * 2 + 1] + st[node * 2 + 2];
    }

    void init(int _n)
    {
        this->n = _n;
        st.resize(4 * n, 0);
        lazy.resize(4 * n, 0);
    }

    long long query(int start, int ending, int l, int r, int node)
    {
        // lazy propagation

        if (lazy[node] != 0)
        {
            if (lazy[node] % 2 == 1)
            {
                int a = st[node];
                st[node] = ending - start + 1 - a;
            }

            if (start != ending)
            {
                lazy[2 * node + 1] += lazy[node];
                lazy[2 * node + 2] += lazy[node];
            }

            lazy[node] = 0;
        }

        // non overlapping case
        if (start > r || ending < l)
        {
            return 0;
        }

        // complete overlap
       else  if (start >= l && ending <= r)
        {
            return st[node];
        }
        else
        {
            int mid = (start+ending) / 2;

            long long q1 = query(start, mid, l, r, 2 * node + 1);
            long long q2 = query(mid + 1, ending, l, r, 2 * node + 2);

            return q1 + q2;
        }
    }

    void update(int start, int ending, int node, int l, int r)
    {
        // lazy propagation

        if (lazy[node] != 0)
        {
            if (lazy[node] % 2 == 1)
            {
                int a = st[node];
                st[node] = ending - start + 1 - a;
            }

            if (start != ending)
            {
                lazy[2 * node + 1] += lazy[node];
                lazy[2 * node + 2] += lazy[node];
            }

            lazy[node] = 0;
        }

        // non overlapping
        if (start > r || ending < l)
        {
            return;
        }

        // complete overlap
        else if (start >= l && ending <= r)
        {
            long long a = st[node];
            st[node] = ending - start + 1 - a;
            if (start != ending)
            {
                lazy[2 * node + 1] += 1;
                lazy[2 * node + 2] += 1;
            }
        }
        else
        {
            int mid = (start+ending) / 2;
            update(start, mid, 2 * node + 1, l, r);
            update(mid + 1, ending, 2 * node + 2, l, r);

            st[node] = st[2 * node + 1] + st[2 * node + 2];
        }
    }
    long long query(int l, int r)
    {
        return query(0, n - 1, l, r, 0);
    }

    void build(vector<int> &v)
    {
        build(0, v.size() - 1, 0, v);
    }
    // add x in range l to r
    void update(int l, int r)
    {
        update(0, n - 1, 0, l, r);
    }
};

class Solution {
public:
    vector<long long> handleQuery(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        segmentTreeLazy tree;
        int n=nums1.size();
    tree.init(n);
    tree.build(nums1);
        vector<long long> ans;
        long long rsum=0;
        for(int i=0;i<nums2.size();i++){
            rsum+=nums2[i];
        }
        for(auto q:queries){
            if(q[0]==1){
                tree.update(q[1],q[2]);

            }else if(q[0]==2){
                rsum+=(tree.query(0,n-1)*q[1]);
            }else {
                ans.push_back(rsum);
            }
        }
        return ans;
    }
};
