                        A[ln].a[i]^=1;
                    }
                }
                else{//Case B
                    for(int i=lm;i<m;++i){
                        if(A[ln].tag==0){
                            if(A[ln].a[i]){
                                --num;
                                --A[ln].sn;
                            }
                            else{
                                ++num;
                                ++A[ln].sn;
                            }
                        }
                        else{
                            if(A[ln].a[i]){
                                ++num;
                                ++A[ln].sn;
                            }
                            else{
                                --num;
                                --A[ln].sn;
                            }
                        }
                        A[ln].a[i]^=1;
                    }
                    for(int i=ln+1;i<=rn-1;++i){
                        int la=A[i].sn;
                        A[i].tag^=1;
                        A[i].sn=m-la;
                        num-=(la-A[i].sn);
                    }
                    for(int i=0;i<=rm;++i){
                        if(A[rn].tag==0){
                            if(A[rn].a[i]){
                                --num;
                                --A[rn].sn;
                            }
                            else{
                                ++num;
                                ++A[rn].sn;
                            }
                        }
                        else{
                            if(A[rn].a[i]){
                                ++num;
                                ++A[rn].sn;
                            }
                            else{
                                --num;
                                --A[rn].sn;
                            }
                        }
                        A[rn].a[i]^=1;
                    }
                }
            }
            else if(q[i][0]==2){
                sum+=(long long)(q[i][1])*num;
            }
            else{//q = 3
                ans.push_back(sum);
            }
        }
        return ans;
    }
};
