        tree = [0]*(4*n)
        lazy = [0]*(4*n)

        def update(v, lo, hi, i, j):
            if lazy[v]:
                tree[v] = (hi - lo + 1) - tree[v]
                if lo < hi:
                    lazy[2*v + 1] ^= lazy[v]
                    lazy[2*v + 2] ^= lazy[v]
                lazy[v] = 0
            if lo > j or hi < i: 
